// این نود دقیقا بعد از HTTP Request قرار می‌گیرد
// و خروجی همون HTTP قبلی در متغیر $json هست.

const post = $json; // همون خروجی /wp/v2/property

// 1) فیلدهای اصلی فارسی
const titleFa   = post?.title?.rendered ?? '';
const contentFa = post?.content?.rendered ?? '';
const slugFa    = post?.slug ?? '';

// 2) ACF خام (از همون WP REST)
const acfRaw = post?.acf || {};

// 3) لیست فیلدهای ACF که می‌خوای ترجمه بشن
// (بر اساس همین خروجی‌ای که فرستادی)
const acfWhitelist = [
  'Duration',           // کوتاه‌مدت
  'occupants',          // چهار نفر
  'address',            // آدرس
  'budget_range',       // low
  'utilities_included', // ["آب","برق",...]
  'target_group',       // ["فقط خانم","فقط دانشجو"]
  'district',           // Wiehre
  'amenities',          // ["پذیرایی","بالکن",...]
  'transport_nearby',   // Tram Holzmarkt
  'description',        // متن طولانی HTML
  'cleaning_fee',       // اگر متنی باشه
  'ودیعه_دپوزیت',      // مقدار ودیعه (اگر خواستی ترجمه بشه)
  // هر فیلد متنی دیگری که خودت می‌خوای…
];

// 4) فقط فیلدهای قابل ترجمه رو از acf جدا می‌کنیم
function pickTranslatableFields(acf, whitelist) {
  const out = {};

  for (const key of Object.keys(acf)) {
    if (!whitelist.includes(key)) continue;

    const value = acf[key];

    // فقط رشته‌ها
    if (typeof value === 'string') {
      out[key] = value;
    }
    // آرایه‌ای از رشته‌ها (مثلاً select چندتایی / checkboxes)
    else if (Array.isArray(value) && value.every(v => typeof v === 'string')) {
      out[key] = value;
    }
    // repeater ساده: آرایه‌ای از آبجکت‌ها که داخلش فیلدهای متنی هست
    else if (Array.isArray(value) && value.every(v => typeof v === 'object' && v !== null)) {
      out[key] = value;
    }
    // عدد، بولین، URL، رابطه (city_ref)، گالری و… رو نمی‌فرستیم برای ترجمه
  }

  return out;
}

const acfForTranslation = pickTranslatableFields(acfRaw, acfWhitelist);

// 5) خروجی تمیز برای ادامه‌ی فلو
return [
  {
    json: {
      id: post.id,

      title_fa:   titleFa,
      content_fa: contentFa,
      slug_fa:    slugFa,

      // کل ACF فارسی (برای merge بعد از ترجمه)
      acf_fa: acfRaw,

      // فقط فیلدهای متنی که میریم ترجمه بشن
      acf_for_translation: acfForTranslation,
    },
  },
];

