// داده‌ی اصلی property از HTTP Request
const data = $json;

// مقدار خام wpml_translations
const rawTranslations = data.wpml_translations;

// یه آرایه‌ی امن می‌سازیم که هر جور ساختاری بود، به آرایه تبدیلش کنیم
let translationsArray = [];

// حالت ۱: خودش آرایه است
if (Array.isArray(rawTranslations)) {
  translationsArray = rawTranslations;
}

// حالت ۲: آبجکت است (مثلاً { en: {...}, de: {...} } یا { translations: [...] })
else if (rawTranslations && typeof rawTranslations === 'object') {
  if (Array.isArray(rawTranslations.translations)) {
    translationsArray = rawTranslations.translations;
  } else {
    // فرض می‌کنیم keyها زبان‌ها هستند و valueها خود ترجمه‌ها
    translationsArray = Object.values(rawTranslations);
  }
}

// حالت ۳: رشته‌ی JSON (مثلاً "\"[{...}...]\"")
else if (typeof rawTranslations === 'string') {
  try {
    const parsed = JSON.parse(rawTranslations);
    if (Array.isArray(parsed)) {
      translationsArray = parsed;
    } else if (parsed && typeof parsed === 'object') {
      if (Array.isArray(parsed.translations)) {
        translationsArray = parsed.translations;
      } else {
        translationsArray = Object.values(parsed);
      }
    }
  } catch (e) {
    // اگر parse نشد، بی‌خیال می‌شیم و آرایه خالی می‌مونه
  }
}

let enId = null;
let deId = null;

// برای دیباگ اگر لازم شد
data._debug_wpml_translations = rawTranslations;

// حالا روی آرایه‌ی امن loop می‌زنیم
for (const t of translationsArray) {
  if (!t || typeof t !== 'object') continue;

  const lang =
    t.language_code ||
    t.language ||
    t.locale ||
    t.code ||
    null;

  const postId =
    t.id ||
    t.element_id ||
    t.post_id ||
    t.translation_id ||
    null;

  if (!postId || !lang) continue;

  if (lang === 'en') {
    enId = postId;
  }
  if (lang === 'de') {
    deId = postId;
  }
}

// فلگ‌ها و شناسه‌ها را به خروجی اضافه می‌کنیم
data.has_en = !!enId;
data.has_de = !!deId;
data.en_id = enId;
data.de_id = deId;

return [
  {
    json: data,
  },
];

let content = '';

// خروجی OpenAI در n8n این‌شکلیه:
// { index: 0, message: { role: 'assistant', content: '...'} }

if ($json.message && typeof $json.message.content === 'string') {
  content = $json.message.content;
} else if (typeof $json.content === 'string') {
  content = $json.content;
} else if (typeof $json === 'string') {
  content = $json;
}

content = (content || '').trim();

// اگر مدل احیاناً ```json ... ``` برگردونه، پاکش می‌کنیم
content = content
  .replace(/^```json/i, '')
  .replace(/^```/, '')
  .replace(/```$/, '')
  .trim();

if (!content) {
  throw new Error('Parse EN Core: empty content from LLM');
}

let data;
try {
  data = JSON.parse(content);
} catch (error) {
  throw new Error(
    'Parse EN Core: JSON parse error: ' +
    error.message +
    '\nRAW:\n' +
    content
  );
}

// خروجی تمیز برای ادامه‌ی فلو
return [
  {
    json: {
      title_en:   data.title_en || '',
      content_en: data.content_en || '',
      slug_en:    data.slug_en   || '',
    },
  },
];

let content = '';

if ($json.message && typeof $json.message.content === 'string') {
  content = $json.message.content;
} else if (typeof $json.content === 'string') {
  content = $json.content;
} else if (typeof $json === 'string') {
  content = $json;
}

content = (content || '').trim();

// اگر مدل ```json ... ``` برگردونه، پاکش می‌کنیم
content = content
  .replace(/^```json/i, '')
  .replace(/^```/, '')
  .replace(/```$/, '')
  .trim();

if (!content) {
  throw new Error('Parse EN ACF: empty content from LLM');
}

let data;
try {
  data = JSON.parse(content);
} catch (error) {
  throw new Error(
    'Parse EN ACF: JSON parse error: ' +
    error.message +
    '\nRAW:\n' +
    content
  );
}

if (!data.acf_en || typeof data.acf_en !== 'object') {
  throw new Error(
    'Parse EN ACF: "acf_en" missing or invalid. Got: ' +
    JSON.stringify(data, null, 2)
  );
}

return [
  {
    json: {
      acf_en: data.acf_en,
    },
  },
];

// از نودهای قبلی داده می‌گیریم
const original      = $node["Prepare Original"].json;          // فارسی + ACF کامل
const core          = $node["Parse EN Core"].json;             // title_en, content_en, slug_en
const acfTranslated = $node["Parse EN ACF"].json;              // acf_en
const translations  = $node["Check Existing Translations"].json || {}; // has_en, en_id, ...

// ACF فارسی کامل
const acfFa = original.acf_fa || {};
// ACF انگلیسی ترجمه‌شده
const acfEn = acfTranslated.acf_en || {};

// مرج: ترجمه‌ها روی نسخه‌ی فارسی override می‌کنند
const acfFinal = {
  ...acfFa,
  ...acfEn,
};

// خروجی نهایی برای ادامه‌ی فلو
return [
  {
    json: {
      original_id: original.id,

      // EN core
      title_en:   core.title_en,
      content_en: core.content_en,
      slug_en:    core.slug_en,

      // فقط برای دیباگ
      acf_en: acfEn,

      // اینو می‌فرستیم برای ACF API
      acf_final: acfFinal,

      // WPML info
      has_en: translations.has_en ?? false,
      en_id:  translations.en_id ?? null,
    },
  },
];
